mapper.select:
SELECT

FROM cdpma_good

    AND category_id = #{categoryId}
    AND good_name LIKE CONCAT('%', #{goodName}, '%')
    AND price >= #{priceLow}
    AND price <= #{priceHigh}

mapper.delete:
DELETE FROM cdpma_good
WHERE good_id IN

    #{id}

mapper.update:
UPDATE cdpma_good
SET like_num = like_num + #{likeDelta}
WHERE good_id = #{goodId}
